def is_server_running():
    for proc in psutil.process_iter(['pid', 'cmdline']):
        if 'python' in proc.cmdline() and '-m' in proc.cmdline() and 'http.server' in proc.cmdline() and '8000' in proc.cmdline():
            return True
    return False

def start_http_server():
    if is_server_running():
        print("HTTP server is already running.")
        return

    screenshot_dir = os.path.abspath("LoggedData/Toshiba/Screenshot")
    command = ["python", "-m", "http.server", "8000", "--directory", screenshot_dir]

    try:
        subprocess.Popen(command, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
        print(f"HTTP server started. Serving files from {screenshot_dir} on port 8000.")
    except Exception as e:
        print(f"Error starting HTTP server: {e}")

def is_server_running(url):
    try:
        response = requests.get(url)
        return response.status_code == 200
    except requests.exceptions.RequestException:
        return False

