$(document).ready(function() {
  let currentIndex;
  const images = $('.screenshot-thumbnail');

  // Image click event to show overlay modal
  $(document).on('click', '.screenshot-thumbnail', function() {
    currentIndex = images.index(this);
    const imageUrl = $(this).data('image');
    const caption = $(this).data('caption');
    $('#overlayImage').attr('src', imageUrl);
    $('#imageCaption').text(caption);
    $('#overlayModal').modal('show');
  });

  // Navigation buttons for overlay modal
  $('#prevImage').click(function() {
    currentIndex = (currentIndex - 1 + images.length) % images.length;
    const prevImage = images[currentIndex];
    const imageUrl = $(prevImage).data('image');
    const caption = $(prevImage).data('caption');
    $('#overlayImage').attr('src', imageUrl);
    $('#imageCaption').text(caption);
  });

  $('#nextImage').click(function() {
    currentIndex = (currentIndex + 1) % images.length;
    const nextImage = images[currentIndex];
    const imageUrl = $(nextImage).data('image');
    const caption = $(nextImage).data('caption');
    $('#overlayImage').attr('src', imageUrl);
    $('#imageCaption').text(caption);
  });
});

