  line-height: 1.5;
  background-color: var(--primary-dark); /* Dark primary color */
  color: var(--secondary-white); /* Secondary color for text */
}

/* Header */
header {
  background-color: var(--primary-darker-blue); /* Darker blue */
  color: var(--secondary-white); /* White text */
  padding: 15px;
  text-align: center;
}

/* Footer */
footer {
  background-color: var(--primary-darker-blue); /* Darker blue */
  color: var(--secondary-white); /* White text */
  text-align: center;
  padding: 15px;
  position: fixed;
  bottom: 0;
  width: 100%;
}

/* User Section */
.user-section {
  background-color: var(--primary-blue); /* Blue */
  color: var(--secondary-white); /* White text */
  margin-bottom: 20px;
  border-radius: 5px;
  padding: 15px;
}

.user-section h2 {
  color: var(--primary-darker-blue); /* Darker blue for headings */
  font-weight: bold;
  border-bottom: 2px solid var(--secondary-green); /* Green */
  padding-bottom: 10px;
}

/* Buttons */
.btn {
  font-family: 'Roboto', sans-serif;
  font-weight: bold;
  background-color: var(--primary-blue); /* Blue */
  color: var(--secondary-white); /* White text */
  border: none;
  padding: 10px 20px;
  cursor: pointer;
  border-radius: 5px;
}

.btn:hover {
  background-color: var(--primary-darker-blue); /* Darker blue on hover */
}

/* Tags */
.tag {
  cursor: pointer;
  padding: 5px 10px;
  background-color: var(--primary-darker-blue); /* Darker blue */
  color: var(--secondary-white); /* White text */
  border-radius: 8px;
  margin-right: 10px;
}

.tag:hover {
  background-color: var(--primary-blue); /* Blue on hover */
}

/* Sidebar */
.sidebar {
  background-color: var(--primary-dark); /* Dark primary color */
  color: var(--secondary-white); /* White text */
  transition: all 0.3s ease;
  z-index: 999;
  overflow-y: auto;
}

.sidebar.active {
  left: 0;
}

/* Navbar */
.navbar {
  background-color: var(--primary-darker-blue); /* Darker blue */
  color: var(--secondary-white); /* White text */
  padding: 15px 10px;
}

/* Adjustments for specific elements can be made as needed */

/* Updated styles for aside element */
aside {
  background-color: var(--primary-dark); /* Dark primary color */
  color: var(--secondary-white); /* White text */
  border-right: 2px solid var(--secondary-green); /* Green border on the right */
}

aside h2 {
  color: var(--primary-darker-blue); /* Darker blue for headings */
  font-weight: bold;
  border-bottom: 2px solid var(--secondary-green); /* Green border at bottom of heading */
  padding-bottom: 10px;
}

aside .user-item {
  background-color: var(--primary-blue); /* Blue */
