  font-weight: 400;
  font-size: small;
  padding: 10px;
  margin-bottom: 5px;
  border-radius: 5px;
  cursor: pointer;
  text-align: center;
}

aside .user-item:hover {
  background-color: var(--primary-darker-blue); /* Darker blue on hover */
}

